INSERT INTO `sp_login_position` VALUES (/*day_count*/3, /*chara_index*/1, /*frame_type*/3, /*frame_position_x*/0, /*frame_position_y*/0, /*text_data*/"美嘉チャンって、\nはじめてのステージは\n憶えてる？");
