INSERT INTO `sp_login_position` VALUES (/*day_count*/4, /*chara_index*/1, /*frame_type*/3, /*frame_position_x*/0, /*frame_position_y*/-20, /*text_data*/"なんかさ、アンカケ\n食べたくなって\nこない～？");
